import React, { useState, useEffect } from "react";

const ChatView = ({ chat, onSendMessage }) => {
  const [userInput, setUserInput] = useState("");
  const [chatHistory, setChatHistory] = useState(chat ? chat.messages : []);

  useEffect(() => {
    if (chat) {
      setChatHistory(chat.messages);
    }
  }, [chat]);

  const handleSendMessage = () => {
    if (userInput.trim() === "") return;

    const newMessage = {
      sender: "user",
      text: userInput,
    };

    // Add user message to chat history
    setChatHistory([...chatHistory, newMessage]);
    onSendMessage(newMessage); // Send message to parent (App.js)

    // Simulate a response from the bot (replace with actual API call)
    const botResponse = {
      sender: "bot",
      text: "This is a response to your query: " + userInput, // Sample bot response
    };

    // Add bot message to chat history
    setChatHistory((prevChat) => [...prevChat, botResponse]);
    onSendMessage(botResponse); // Send bot response to parent (App.js)
    setUserInput(""); // Reset the input field
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        height: "0%",
        justifyContent: "space-between", // This makes sure the text box stays at the bottom
      }}
    >
      <div
        style={{
          maxHeight: "80vh", // Set the max height for the chat area
          overflowY: "auto", // Enable scroll for the chat messages
          paddingBottom: "10px", // Prevent text box overlap with messages
        }}
      >
        {chatHistory.map((message, index) => (
          <div key={index} style={{ marginBottom: "10px" }}>
            <strong>{message.sender === "user" ? "You" : "Bot"}:</strong>
            <p>{message.text}</p>
          </div>
        ))}
      </div>

      {/* Textbox for user input, fixed at the bottom */}
      <div style={{ padding: "10px" }}>
        <textarea
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          placeholder="Type your message..."
          rows="3"
          style={{
            width: "100%",
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            boxSizing: "border-box", // Ensure width accounts for padding
          }}
        ></textarea>
        <button
          onClick={handleSendMessage}
          style={{
            width: "100%",
            padding: "10px",
            marginTop: "10px",
            backgroundColor: "#4CAF50",
            color: "white",
            border: "none",
            cursor: "pointer",
            borderRadius: "5px",
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatView;
