import React, { useState } from "react";
import FileUpload from "./components/FileUpload";
import ChatView from "./components/ChatView";

const App = () => {
  const [currentChat, setCurrentChat] = useState(null); // Store the current chat

  return (
    <div style={{ display: "flex", flexDirection: "column", height: "100vh" }}>
      {/* Title at the top-left */}
      <h1 style={{ color: "#4CAF50", marginLeft: "10px" }}>Chatbot</h1>

      <div style={{ display: "flex", flex: 1, overflow: "hidden" }}>
        {/* Left panel for file upload */}
        <div style={{ width: "30%", padding: "10px" }}>
          <h3>Upload Documents</h3>
          <FileUpload onFileUpload={(newConversation) => setCurrentChat(newConversation)} />
        </div>

        {/* Middle panel for displaying the current chat */}
        <div style={{ width: "70%", padding: "10px", display: "flex", flexDirection: "column", overflowY: "auto" }}>
          {/* Conversation area above the text box */}
          <div style={{ flex: 1, overflowY: "auto", paddingRight: "10px" }}>
            <h3>Chat</h3>
            <ChatView
              chat={currentChat}
              onSendMessage={(message) => {
                setCurrentChat((prev) => ({
                  ...prev,
                  messages: prev ? [...prev.messages, message] : [message]
                }));
              }}
            />
          </div>

          {/* Text box at the bottom */}
          <div style={{ marginTop: "auto" }}>
            {/* You can add your TextBox or Input component here */}
            <input
              type="text"
              placeholder="Type your message..."
              style={{
                width: "100%",
                padding: "10px",
                fontSize: "16px",
                borderRadius: "5px",
                border: "1px solid #ccc"
              }}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
