import React, { useState } from "react";
import FileUpload from "./components/FileUpload";
import ChatView from "./components/ChatView";
import ChatPanel from "./components/ChatPanel";

const App = () => {
  const [conversations, setConversations] = useState([]);  // Store previous conversations
  const [currentChat, setCurrentChat] = useState(null);    // Store the current chat

  const handleFileUpload = (newConversation) => {
    // Add a new conversation to the history when a file is uploaded
    setConversations([...conversations, newConversation]);
    setCurrentChat(newConversation); // Set the uploaded conversation as current
  };

  const handleChatClick = (chatId) => {
    // Set the clicked conversation as the current chat
    const chat = conversations.find((conversation) => conversation.chatId === chatId);
    setCurrentChat(chat);
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* Left panel for displaying previous conversations */}
      <div style={{ width: "20%", padding: "10px", borderRight: "1px solid #ccc", overflowY: "auto" }}>
      <h1 style={{ color: "#4CAF50" }}>Chatbot</h1>
        <ChatPanel conversations={conversations} onChatClick={handleChatClick} />
      </div>

      {/* Middle panel for displaying the current chat */}
      <div style={{ width: "50%", padding: "10px", borderRight: "1px solid #ccc", overflowY: "auto" }}>
        <h3>Chat</h3>
        <ChatView
          chat={currentChat}
          onSendMessage={(message) => {
            setCurrentChat((prev) => {
              const newChat = { ...prev, messages: [...prev.messages, message] };
              return newChat;
            });
          }}
        />
      </div>

      {/* Right panel for file upload */}
      <div style={{ width: "30%", padding: "10px" }}>
        <h3>Upload Documents</h3>
        <FileUpload onFileUpload={handleFileUpload} />
      </div>
    </div>
  );
};

export default App;
