import React, { useState } from "react";
import ChatView from "./ChatView";

const App = () => {
  const [chat, setChat] = useState({
    messages: [],
  });

  const handleSendMessage = (message) => {
    // Update the chat history in the parent (App.js)
    setChat((prevChat) => ({
      messages: [...prevChat.messages, message],
    }));
  };

  return (
    <div
      style={{
        fontFamily: "'Roboto', sans-serif", // Clean and modern font
        display: "flex",
        justifyContent: "center", // Center content horizontally
        alignItems: "center", // Center content vertically
        height: "100vh", // Full height to center vertically
        backgroundColor: "#f1f1f1", // Light gray background for the whole app
        margin: 0, // Remove default margin
      }}
    >
      <div
        style={{
          width: "100%",
          maxWidth: "500px", // Limit the max width of the chat container
          backgroundColor: "#fff", // White background for the chat container
          borderRadius: "10px", // Rounded corners
          boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)", // Soft shadow for 3D effect
          overflow: "hidden", // Ensure no overflow outside the container
        }}
      >
        <div
          style={{
            padding: "20px",
            borderBottom: "1px solid #ddd", // Divider line
            backgroundColor: "#4CAF50", // Header background color
            color: "white", // White text color for the header
            fontSize: "20px",
            fontWeight: "bold",
            textAlign: "center", // Center the header text
            borderTopLeftRadius: "10px", // Rounded corners on top
            borderTopRightRadius: "10px", // Rounded corners on top
          }}
        >
          Chat with Bot
        </div>
        <ChatView chat={chat} onSendMessage={handleSendMessage} />
      </div>
    </div>
  );
};

export default App;
