import React, { useState } from "react";
import Dropzone from "react-dropzone";
import axios from "axios";

const FileUpload = ({ onFileUpload }) => {
  const [files, setFiles] = useState([]);
  const [uploadStatus, setUploadStatus] = useState("");

  const handleDrop = (acceptedFiles) => {
    setFiles(acceptedFiles);
  };

  const handleUpload = async () => {
    if (files.length === 0) return;

    const formData = new FormData();
    files.forEach((file) => formData.append("files", file));

    try {
      setUploadStatus("Uploading...");
      const res = await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      // Assuming the server returns the chat id or conversation metadata
      const chatId = res.data.chatId; // You can adjust this based on your API response
      const newConversation = {
        chatId,
        files: files.map((file) => file.name),
        chatHistory: "Chat history will be loaded here...", // Placeholder
      };

      // Notify parent component to add the new conversation
      onFileUpload(newConversation);

      setUploadStatus("Upload successful!");
    } catch (error) {
      console.error(error);
      setUploadStatus("Upload failed.");
    }
  };

  return (
    <div style={{ padding: "20px", border: "1px solid #ccc", margin: "10px" }}>
      <h3>Upload Documents</h3>
      <Dropzone onDrop={handleDrop}>
        {({ getRootProps, getInputProps }) => (
          <div
            {...getRootProps()}
            style={{
              border: "2px dashed #ccc",
              padding: "20px",
              textAlign: "center",
              cursor: "pointer",
            }}
          >
            <input {...getInputProps()} />
            <p>Drag and drop files here, or click to select files</p>
          </div>
        )}
      </Dropzone>

      {files.length > 0 && (
        <div style={{ marginTop: "10px" }}>
          <h4>Selected Files:</h4>
          <ul>
            {files.map((file, index) => (
              <li key={index}>{file.name}</li>
            ))}
          </ul>
        </div>
      )}

      <button onClick={handleUpload} style={{ marginTop: "10px" }}>
        Upload
      </button>
      {uploadStatus && <p>{uploadStatus}</p>}
    </div>
  );
};

export default FileUpload;
