import React, { useState } from "react";

const ChatView = ({ chat }) => {
  const [userInput, setUserInput] = useState("");
  const [chatHistory, setChatHistory] = useState(chat.messages || []);

  const handleSendMessage = () => {
    if (userInput.trim() === "") return;

    const newMessage = {
      sender: "user",
      text: userInput,
    };

    // Add user message to chat history
    setChatHistory([...chatHistory, newMessage]);

    // Simulate a response from the bot (you can replace this with API call)
    const botResponse = {
      sender: "bot",
      text: "This is a response to your query: " + userInput, // Sample bot response
    };

    // Add bot message to chat history
    setChatHistory((prevChat) => [...prevChat, botResponse]);
    setUserInput("");
  };

  return (
    <div style={{ padding: "10px", maxHeight: "80vh", overflowY: "auto" }}>
      <div>
        {chatHistory.map((message, index) => (
          <div key={index} style={{ marginBottom: "10px" }}>
            <strong>{message.sender === "user" ? "You" : "Bot"}:</strong>
            <p>{message.text}</p>
          </div>
        ))}
      </div>

      <div>
        <textarea
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          placeholder="Type your message..."
          rows="3"
          style={{ width: "100%", padding: "10px", marginTop: "10px" }}
        ></textarea>
        <button
          onClick={handleSendMessage}
          style={{
            padding: "10px",
            marginTop: "10px",
            backgroundColor: "#4CAF50",
            color: "white",
            border: "none",
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatView;
