import React, { useState } from "react";
import FileUpload from "./components/FileUpload";
import ChatView from "./components/ChatView";

const App = () => {
  const [currentChat, setCurrentChat] = useState(null); // Store the current chat

  return (
    // Example styling adjustments
<div style={{ display: "flex", flexDirection: "column", height: "100vh", fontFamily: "'Roboto', sans-serif" }}>
  <div style={{ backgroundColor: "#333", padding: "20px", boxShadow: "0 4px 8px rgba(0,0,0,0.1)" }}>
    <h1 style={{ color: "#8CE069", fontSize: "2.5em", fontWeight: "600" }}>Chatbot</h1>
  </div>

  <div style={{ display: "flex", flex: 1, overflow: "hidden" }}>
    <div style={{ width: "30%", padding: "20px", borderRight: "1px solid #ddd", backgroundColor: "#f5f5f5" }}>
      <h2 style={{ color: "#333", fontWeight: "500" }}>Upload Documents</h2>
      <FileUpload onFileUpload={(newConversation) => setCurrentChat(newConversation)} />
    </div>

    <div
      style={{
        width: "70%",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        backgroundColor: "#fff",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        borderRadius: "8px",
      }}
    >
      <div style={{ flex: 1, overflowY: "auto", paddingRight: "10px", marginBottom: "20px" }}>
        <h2 style={{ color: "#333", fontWeight: "500" }}>Chat</h2>
        <ChatView
          chat={currentChat}
          onSendMessage={(message) => {
            setCurrentChat((prev) => ({
              ...prev,
              messages: prev ? [...prev.messages, message] : [message],
            }));
          }}
        />
      </div>
    </div>
  </div>
</div>

  );
};

export default App;
