import React, { useState } from "react";
import FileUpload from "./components/FileUpload";
import ChatView from "./components/ChatView";

const App = () => {
  const [currentChat, setCurrentChat] = useState(null);  // Store the current chat

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      <h1 style={{ color: "#4CAF50" }}>Chatbot</h1>
      {/* Middle panel for displaying the current chat */}
      <div style={{ width: "70%", padding: "10px", borderRight: "1px solid #ccc", overflowY: "auto" }}>
        <h3>Chat</h3>
        <ChatView
          chat={currentChat}
          onSendMessage={(message) => {
            setCurrentChat((prev) => ({
              ...prev,
              messages: prev ? [...prev.messages, message] : [message]
            }));
          }}
        />
      </div>

      {/* Right panel for file upload */}
      <div style={{ width: "30%", padding: "10px" }}>
        <h3>Upload Documents</h3>
        <FileUpload onFileUpload={(newConversation) => setCurrentChat(newConversation)} />
      </div>
    </div>
  );
};

export default App;
