import React, { useState, useEffect } from "react";

const ChatView = ({ chat, onSendMessage }) => {
  const [userInput, setUserInput] = useState("");
  const [chatHistory, setChatHistory] = useState(chat ? chat.messages : []);

  useEffect(() => {
    if (chat) {
      setChatHistory(chat.messages);
    }
  }, [chat]);

  const handleSendMessage = async () => {
    if (userInput.trim() === "") return;

    const newMessage = {
      sender: "user",
      text: userInput,
    };

    // Add user message to chat history
    setChatHistory([...chatHistory, newMessage]);
    onSendMessage(newMessage); // Send message to parent (App.js)

    // Make API call to fetch bot's response (replace with your actual API endpoint)
    try {
      const response = await fetch("http://localhost:5000/getBotResponse", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ userInput: userInput }),
      });

      if (response.ok) {
        const data = await response.json();
        const botResponse = {
          sender: "bot",
          text: data.response, // Assuming the response contains a 'response' field
        };

        // Add bot response to chat history
        setChatHistory((prevChat) => [...prevChat, botResponse]);
        onSendMessage(botResponse); // Send bot response to parent (App.js)
      } else {
        const botResponse = {
          sender: "bot",
          text: "Sorry, I couldn't process your request at the moment.", // Fallback message
        };

        // Add fallback response to chat history
        setChatHistory((prevChat) => [...prevChat, botResponse]);
        onSendMessage(botResponse); // Send bot response to parent (App.js)
      }
    } catch (error) {
      console.error("Error fetching bot response:", error);
      const botResponse = {
        sender: "bot",
        text: "Oops! Something went wrong. Please try again later.", // Error message
      };

      // Add error response to chat history
      setChatHistory((prevChat) => [...prevChat, botResponse]);
      onSendMessage(botResponse); // Send bot response to parent (App.js)
    }

    setUserInput(""); // Reset the input field
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        height: "89%",
        justifyContent: "space-between", // This makes sure the text box stays at the bottom
      }}
    >
      <div
        style={{
          maxHeight: "80vh", // Set the max height for the chat area
          overflowY: "auto", // Enable scroll for the chat messages
          paddingBottom: "10px", // Prevent text box overlap with messages
        }}
      >
        {chatHistory.map((message, index) => (
          <div key={index} style={{ marginBottom: "10px" }}>
            <strong>{message.sender === "user" ? "You" : "Bot"}:</strong>
            <p>{message.text}</p>
          </div>
        ))}
      </div>

      {/* Textbox for user input, fixed at the bottom */}
      <div style={{ padding: "10px" }}>
        <textarea
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          placeholder="Type your message..."
          rows="3"
          style={{
            width: "100%",
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            boxSizing: "border-box", // Ensure width accounts for padding
          }}
        ></textarea>
        <button
          onClick={handleSendMessage}
          style={{
            width: "100%",
            padding: "10px",
            marginTop: "10px",
            backgroundColor: "#4CAF50",
            color: "white",
            border: "none",
            cursor: "pointer",
            borderRadius: "5px",
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatView;
