import React, { useState } from "react";
import axios from "axios";

const ChatQuery = () => {
  const [query, setQuery] = useState("");
  const [response, setResponse] = useState("");
  const [responsesHistory, setResponsesHistory] = useState([]); // To store all responses
  const [loading, setLoading] = useState(false);

  const handleQuerySubmit = async () => {
    if (!query) return;

    setLoading(true);
    setResponse("");
    try {
      // Construct the context by joining all previous responses
      const context = responsesHistory.join(" ");
      const fullQuery = `${context} ${query}`; // Combine the context with the new query

      const res = await axios.post("http://localhost:5000/query", { query: fullQuery });
      const modelResponse = res.data.response;

      // Update response state and responses history
      setResponse(modelResponse);
      setResponsesHistory([...responsesHistory, modelResponse]); // Append the new response to history
    } catch (error) {
      console.error(error);
      setResponse("An error occurred while processing your query.");
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: "20px", border: "1px solid #ccc", margin: "10px" }}>
      <h3>Ask a Question</h3>
      <textarea
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Type your question here..."
        rows="4"
        style={{ width: "100%", padding: "10px" }}
      ></textarea>
      <button onClick={handleQuerySubmit} style={{ marginTop: "10px" }}>
        Submit
      </button>

      {loading ? (
        <p>Loading...</p>
      ) : (
        response && <p><strong>Response:</strong> {response}</p>
      )}
    </div>
  );
};

export default ChatQuery;
