import React, { useState } from "react";
import FileUpload from "./componentsFileUpload";
import ChatPanel from "./ChatPanel";
import ChatView from "./ChatView";

const ChatApp = () => {
  const [conversations, setConversations] = useState([]);
  const [currentChat, setCurrentChat] = useState(null);

  const handleFileUpload = (newConversation) => {
    setConversations([...conversations, newConversation]);
  };

  const handleChatClick = (chatId) => {
    const chat = conversations.find((conversation) => conversation.chatId === chatId);
    setCurrentChat(chat);
  };

  return (
    <div style={{ display: "flex", padding: "20px", border: "1px solid #ccc" }}>
      {/* Left panel for conversations */}
      <ChatPanel conversations={conversations} onChatClick={handleChatClick} />

      {/* Right panel for uploading files and viewing chat history */}
      <ChatView chat={currentChat} />

      {/* Upload documents component */}
      <FileUpload onFileUpload={handleFileUpload} />
    </div>
  );
};

export default ChatApp;
